import React, { useState, useEffect } from 'react';
import logo from './IconeTwitch.svg';
import search from './Search.svg';
import menuIco from './MenuIco.svg';
import { Link } from 'react-router-dom';

const Header = () => {

    const [menu, showMenu] = useState(false);
    const [smallScreen, setSmallScreen] = useState(false);

    useEffect(() => {
        const mediaQuery = window.matchMedia("(max-width: 900px)");

        //addListener = addEventListener pour mediaQuery en js
        mediaQuery.addListener(handleMediaQueryChange);

        handleMediaQueryChange(mediaQuery);

        return () => {
            mediaQuery.removeListener(handleMediaQueryChange);
        }
    });

    const handleMediaQueryChange = mediaQuery => {
        if(mediaQuery.matches) {
            setSmallScreen(true);
        } else {
            setSmallScreen(false);
        }
    }

    const toggleNavRes = () => {
        showMenu(!menu);
    }

    return(
        <div className="headerTop">
            <nav>
                {menu && 
                    <ul className="listeMenu">
                        <li className="liensNav">
                            <Link className="lien" to="/">
                                <img src={logo} alt="logo-twitch" className="logo"/>
                            </Link> 
                        </li>
                        <li className="liensNav">
                            <Link className="lien" to="/">
                                Top Games
                            </Link>
                        </li>
                        <li className="liensNav">
                            <Link className="lien" to="/top-streams">
                                Top Streams
                            </Link>
                        </li>
                        <li className="liensNav">
                            <form className="formSubmit">
                                <input type="text" className="inputRecherche"/>
                                <button type="submit">
                                <img src={search} alt="icone loupe" className="logoLoupe"/>
                                </button>
                            </form>
                        </li>
                    </ul>
                }
            </nav>

            <div className="menuResBtn">
                <img onClick={toggleNavRes} src={menuIco} alt="icone menu" className="menuIco"/>
            </div>
        </div>
    )
};

export default Header;
